#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>
#include <numeric>
#include <iomanip>
#include <string>

using namespace std;

// Helper function to simplify fractions
pair<int, int> simplify(int numerator, int denominator) {
    int g = gcd(numerator, denominator);
    return {numerator / g, denominator / g};
}

// Function to format output for rational numbers
string formatRational(int p, int q) {
    auto [num, denom] = simplify(p, q);
    if (denom == 1) {
        return to_string(num);
    } else {
        return to_string(num) + "/" + to_string(denom);
    }
}

// Function to process each equation
string processEquation(int a, int b, int c) {
    int delta = b * b - 4 * a * c;

    if (delta < 0) {
        return "NO";  // No real solutions
    }

    // Calculate the larger root
    int sqrtDelta = static_cast<int>(sqrt(delta));
    int x1_numerator = -b + sqrtDelta; // Larger root numerator
    int x2_numerator = -b - sqrtDelta; // Smaller root numerator

    // Denominator (which is always positive)
    int denominator = 2 * a;

    // The larger root
    int x_num = max(x1_numerator, x2_numerator);

    // If delta is a perfect square, it's a rational number
    if (delta == sqrtDelta * sqrtDelta) {
        return formatRational(x_num, denominator);
    }

    // Otherwise, we have an irrational number
    int q1 = x_num / denominator;
    int q2 = x_num % denominator;

    // Analyze the remainder:
    if (q2 == 0) {
        return to_string(q1); // Just the integer part
    }

    // sqrt(delta) represented as a fraction (sqrtDelta / (2a))
    int r = delta;
    if (q1 != 0) {
        return to_string(q1) + "+" + formatRational(sqrtDelta, denominator);
    } else {
        return formatRational(sqrtDelta, denominator);
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    int T, M;
    cin >> T >> M;
    vector<string> results;

    for (int i = 0; i < T; ++i) {
        int a, b, c;
        cin >> a >> b >> c;
        results.push_back(processEquation(a, b, c));
    }

    for (const auto &result : results) {
        cout << result << "\n";
    }

    return 0;
}
